import com.eviware.soapui.support.types.StringToStringMap import net.sf.* import net.sf.json.* import net.sf.json.groovy.*
def headers = new StringToStringMap() def ResponseMessage = testRunner.testCase.testSteps["Autenticar"].testRequest.response.contentAsString def token def jsonSlurper = new JsonSlurper().parseText(ResponseMessage)
token = jsonSlurper."token-acesso" headers.put("Authorization", "OAuth2 " + token) headers.put("ig-consumidor", "") headers.put("ig-requestid", "") testRunner.testCase.getTestStepByName("Notificar").testRequest.setRequestHeaders(headers)

